import React, { useState } from 'react';
import { Navbar } from './components/Navbar';
import { GameCard } from './components/GameCard';
import { AdminLayout } from './components/admin/AdminLayout';
import { DataTable } from './components/admin/DataTable';

const mockUsers = [
    { id: 1, name: 'John Doe', email: 'john@example.com', status: 'Active' },
    { id: 2, name: 'Jane Smith', email: 'jane@example.com', status: 'Active' },
];

const mockStaff = [
    { id: 1, name: 'Admin User', role: 'Administrator', department: 'Management' },
    { id: 2, name: 'Staff User', role: 'Customer Service', department: 'Support' },
];

const mockGames = [
    { id: 1, title: 'Cyberpunk 2077', stock: 5, price: 14.99, status: 'Available' },
    { id: 2, title: 'Elden Ring', stock: 3, price: 15.99, status: 'Available' },
];

export const AdminDashboard = () => {
    const [currentSection, setCurrentSection] = useState('dashboard');

    const renderAdminSection = () => {
        switch (currentSection) {
            case 'dashboard':
                return (
                    <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-8">
                        {[
                            { title: 'Total Users', value: '1,234' },
                            { title: 'Active Rentals', value: '56' },
                            { title: 'Total Games', value: '89' },
                        ].map((stat) => (
                            <div key={stat.title} className="bg-gray-800 p-6 rounded-lg">
                                <h3 className="text-gray-400 text-sm">{stat.title}</h3>
                                <p className="text-white text-2xl font-bold">{stat.value}</p>
                            </div>
                        ))}
                    </div>
                );
            case 'users':
                return (
                    <div>
                        <h2 className="text-2xl font-bold text-white">Users Management</h2>
                        <DataTable headers={['ID', 'Name', 'Email', 'Status']} data={mockUsers} />
                    </div>
                );
            case 'staff':
                return (
                    <div>
                        <h2 className="text-2xl font-bold text-white">Staff Management</h2>
                        <DataTable headers={['ID', 'Name', 'Role', 'Department']} data={mockStaff} />
                    </div>
                );
            case 'games':
                return (
                    <div>
                        <h2 className="text-2xl font-bold text-white">Games Management</h2>
                        <DataTable headers={['ID', 'Title', 'Stock', 'Price', 'Status']} data={mockGames} />
                    </div>
                );
            default:
                return null;
        }
    };

    return (
        <AdminLayout currentSection={currentSection} onSectionChange={setCurrentSection}>
            {renderAdminSection()}
        </AdminLayout>
    );
};
