import React, { useState } from 'react';
import { ProfileSelection } from './ProfileSelection';
import { AdminDashboard } from './AdminDashboard';
import { Container, Typography, Paper, Box } from '@material-ui/core';

function App() {
    const [selectedProfile, setSelectedProfile] = useState(null);

    const handleProfileSelect = (profile) => {
        setSelectedProfile(profile);
    };

    if (!selectedProfile) {
        return (
            <Container maxWidth="sm">
                <Paper elevation={3} style={{ padding: '20px', marginTop: '20px' }}>
                    <ProfileSelection onProfileSelect={handleProfileSelect} />
                </Paper>
            </Container>
        );
    }

    if (selectedProfile.name === 'Profile 1') {
        return <AdminDashboard />;
    }

    // Add conditions for other profiles if needed

    return (
        <Container maxWidth="sm">
            <Paper elevation={3} style={{ padding: '20px', marginTop: '20px' }}>
                <Box textAlign="center">
                    <Typography variant="h4" gutterBottom>
                        Welcome, {selectedProfile.name}!
                    </Typography>
                    <Typography variant="body1">
                        {selectedProfile.description}
                    </Typography>
                </Box>
            </Paper>
        </Container>
    );
}

export default App;
