import React, { useState } from 'react';
import './mainPage.css';

const profiles = [
    { id: 1, name: 'Profile 1', description: 'This is profile 1' },
    { id: 2, name: 'Profile 2', description: 'This is profile 2' },
    { id: 3, name: 'Profile 3', description: 'This is profile 3' }
];

const ProfileCard = ({ profile, onSelect }) => (
    <div className="profile-card">
        <h2>{profile.name}</h2>
        <p>{profile.description}</p>
        <button onClick={() => onSelect(profile)}>Select</button>
    </div>
);

const MainPage = ({ onSelect }) => (
    <div className="main-page">
        <h1>Select a Profile</h1>
        <div className="profiles-container">
            {profiles.map(profile => (
                <ProfileCard key={profile.id} profile={profile} onSelect={onSelect} />
            ))}
        </div>
    </div>
);

function App() {
    const [selectedProfile, setSelectedProfile] = useState(null);

    const handleProfileSelect = (profile) => {
        setSelectedProfile(profile);
    };

    if (selectedProfile) {
        return (
            <div className="profile-details">
                <h1>Welcome, {selectedProfile.name}!</h1>
                <p>{selectedProfile.description}</p>
                <button onClick={() => setSelectedProfile(null)}>Back to Profiles</button>
            </div>
        );
    }

    return <MainPage onSelect={handleProfileSelect} />;
}

export default App;
