import React, { useState } from 'react';
import { ProfileSelection } from './ProfileSelection';
import { AdminDashboard } from './AdminDashboard';
import { Container, Typography, Paper, Box, Card, CardContent, Button } from '@mui/material';

function App() {
    const [selectedProfile, setSelectedProfile] = useState(null);

    const handleProfileSelect = (profile) => {
        setSelectedProfile(profile);
    };

    if (!selectedProfile) {
        return (
            <Container maxWidth="md" style={{ marginTop: '50px' }}>
                <Typography variant="h4" gutterBottom align="center" color="primary">
                    Select a Profile
                </Typography>
                <Grid container spacing={3}>
                    {[
                        { id: 1, name: 'Profile 1', description: 'Admin Dashboard Access' },
                        { id: 2, name: 'Profile 2', description: 'User Dashboard Access' },
                        { id: 3, name: 'Profile 3', description: 'Guest Dashboard Access' },
                    ].map((profile) => (
                        <Grid item xs={12} sm={6} md={4} key={profile.id}>
                            <Card elevation={3}>
                                <CardContent>
                                    <Typography variant="h6" color="textPrimary">
                                        {profile.name}
                                    </Typography>
                                    <Typography variant="body2" color="textSecondary" paragraph>
                                        {profile.description}
                                    </Typography>
                                    <Button
                                        variant="contained"
                                        color="primary"
                                        fullWidth
                                        onClick={() => handleProfileSelect(profile)}
                                    >
                                        Select
                                    </Button>
                                </CardContent>
                            </Card>
                        </Grid>
                    ))}
                </Grid>
            </Container>
        );
    }

    if (selectedProfile.name === 'Profile 1') {
        return <AdminDashboard />;
    }

    return (
        <Container maxWidth="sm" style={{ marginTop: '50px' }}>
            <Paper elevation={4} style={{ padding: '20px' }}>
                <Box textAlign="center">
                    <Typography variant="h4" gutterBottom color="primary">
                        Welcome, {selectedProfile.name}!
                    </Typography>
                    <Typography variant="body1" color="textSecondary">
                        {selectedProfile.description}
                    </Typography>
                    <Button
                        variant="contained"
                        color="secondary"
                        style={{ marginTop: '20px' }}
                        onClick={() => setSelectedProfile(null)}
                    >
                        Back to Profiles
                    </Button>
                </Box>
            </Paper>
        </Container>
    );
}

export default App;
